public class Do_While_2 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=10;
		do {
			System.out.println("I am happy");
			i++;
		}
		while(i<11);

	}

}
